INSERT INTO `barberiadb`.`service` (`thumbnail`,`available`,`description`, `discount_price`, `discount`, `original_price`, `service_name`, `time_consume`) VALUES ('http://localhost:8080/files/combo_cat_goi.jpg',1,'Với combo VIP CẮT GỘI này, ngoài việc được làm đầy đủ 10 bước của Shinecombo, anh còn được sử dụng thêm 3 bước VIP giúp chăm sóc toàn diện khuôn mặt. Đặc biệt là bước đắp mặt nạ vàng 24k', 180, 0, 199, 'COMBO CẮT GỘI', 60);
